INSERT INTO public.iniciativa_actor VALUES (35, 'Secretara de Educacin Superior, Ciencia, Tecnologa e Innovacin', 'SENESCYT', 'https://www.educacionsuperior.gob.ec/', 'La Secretara de Educacin Superior, Ciencia, Tecnologa e Innovacin, es la entidad del gobierno ecuatoriano que ejerce la rectora de la poltica publica en los ejes de su competencia. Tiene como misin coordinar acciones entre la Funcin Ejecutiva y las instituciones del Sistema de Educacin Superior.', '2020-12-20 12:49:07', '2020-12-20 12:49:07', NULL);
INSERT INTO public.iniciativa_actor VALUES (36, 'FabLab ZOI', NULL, 'http://www.fablabzoi.org/', 'FabLab ZOI es un laboratorio de investigacin y desarrollo tecnolgico que promueve ciencia, diseo y arte como centro del desarrollo humano para mejorar la calidad de vida en Latinoamrica. Nace en 2014 en la ciudad de Quito y ha generado proyectos de impacto en Ecuador y Latinoamrica enfocados en la inclusin y democratizacin de conocimientos y tecnologas emergentes. Somos parte de FabLab Network, red global de laboratorios que promueven la educacin de calidad de manera distribuida y el empoderamiento tecnolgico. Donde se generan programas educativos liderados por los intelectuales ms destacados del mundo en diferentes reas del conocimiento.', '2020-12-20 13:04:54', '2020-12-20 13:04:54', NULL);
