INSERT INTO public.iniciativa_actor VALUES (45, 'Corporacin para el Desarrollo del Emprendimiento y la Innovacin Social', 'CODEIS', 'https://www.somoscodeis.org/', 'CODEIS es una empresa social que empodera y prepara a las personas de los grupos vulnerables en la base de la pirmide para transformar sus comunidades sin intervenciones externas. CODEIS ayuda a estas comunidades a travs de capacitaciones en emprendimiento social, conexiones a recursos y capital, y tutora a medida que desarrollan sus negocios.', '2020-12-20 21:36:40', '2020-12-20 21:36:40', NULL);
INSERT INTO public.iniciativa_actor VALUES (48, 'Fundacin Futuro Latinoamericano', 'FFLA', 'https://www.ffla.net/', 'FFLA nace como una respuesta a las necesidades de la regin identificadas en la Conferencia de las Naciones Unidas sobre Medio Ambiente y Desarrollo celebrada en Ro de Janeiro en 1992, denominada tambin Cumbre de la Tierra, que se resumen en la clara necesidad de un liderazgo en la discusin sobre desarrollo sostenible que se oriente a largo plazo, y la necesidad de incorporar el dilogo para la articulacin e integracin de las negociaciones de la regin', '2020-12-21 07:31:42', '2020-12-21 07:31:42', NULL);
