INSERT INTO public.iniciativa_actor VALUES (55, 'Facultad Latinoamericana de Ciencias Sociales Sede Ecuador', 'FLACSO', 'https://www.flacso.edu.ec/portal/index.php?module=academico&func=display&table=oferta_academica&id=1&lang=es', 'FLACSO Ecuador construye y difunde conocimientos y saberes en el campo de las ciencias sociales y las humanidades a travs de la docencia y la investigacin crtica y creativa. Sus principios son el pluralismo y la autonoma acadmica en la bsqueda de contribuir al desarrollo de las diversas corrientes del pensamiento latinoamericano y andino, y de promover la justicia, la equidad y la inclusin social.', '2020-12-21 08:35:25', '2020-12-21 08:35:25', NULL);
INSERT INTO public.iniciativa_actor VALUES (56, 'Univarsidad Catlica de Santiago de Guayaquil', 'UCSG', 'https://www.ucsg.edu.ec/', 'La Universidad Catlica de Santiago de Guayaquil es un establecimiento de educacin superior, que como tal tiene como finalidades esenciales la preparacin de profesionales socialmente responsables a base de la investigacin, conservacin, promocin y difusin de la ciencia y de la cultura, haciendo nfasis en sus valores autctonos con miras a lograr el mejor desarrollo y superacin del hombre ecuatoriano en un marco de convivencia democrtica, justicia social, paz creadora, respeto y exaltacin a los valores y derechos humanos, asegurando al propio tiempo, de una manera institucional, la impronta de una genuina inspiracin cristiana y el mensaje de Cristo', '2020-12-21 08:38:19', '2020-12-21 08:38:19', NULL);
