INSERT INTO public.iniciativa_actor VALUES (62, 'Stratega BDS (Business Development Services', NULL, 'http://www2.stratega.com.ec/', 'Stratega BDS (Business Development Services) fue fundada en el 2002 por Nathalie Cely y Jos Mantilla, profesionales con slida experiencia en las reas de desarrollo empresarial, estructuracin financiera, competitividad y financiamiento de proyectos privados. STRATEGA BDS naci con el objetivo de apoyar a empresas innovadoras a crecer y exportar, a travs de asesora e inteligencia estratgica, as como la provisin de servicios de desarrollo empresarial. De igual manera, Stratega ha contribuido a formar un clima de negocios competitivo a travs de la generacin de polticas de competitividad y de desarrollo de cadenas productivas.', '2020-12-21 09:12:26', '2020-12-21 09:12:26', NULL);
