INSERT INTO public.iniciativa_actor VALUES (66, 'Workshop Coworking', NULL, 'https://www.linkedin.com/company/workshopcoworking/', 'Workshop Coworking es fundado para ayudar a nuevos emprendedores y freelancers de Guayaquil a impulsar sus negocios ofreciendo espacios de trabajo colaborativo y salas de juntas diseados a la medida y con los mejores precios. Dentro de Workshop Coworking nos enfocamos tanto en el diseo como en la funcionalidad, de tal modo que todos los elementos que conforman nuestros espacios de trabajo incentivan a la creatividad y a mejorar el desempeo de nuestros miembros.', '2020-12-21 09:24:15', '2020-12-21 09:24:15', NULL);
