INSERT INTO public.iniciativa_actor VALUES (68, 'Centro de Innovacin Rokk3r IDE', NULL, 'https://ide.rokk3r.com/', 'Somos un centro de innovacin que desarrolla cursos y programas para emprendedores y empresas con el fin de facilitar la creacin de nuevos negocios y startups e impulsar la innovacin exponencial al interior de las empresas. ROKK3R IDE nace como resultado del esfuerzo colaborativo entre Rokk3r y el IDE Business School con el propsito principal de empoderar a ejecutivos y emprendedores a desarrollar una mentalidad exponencial que les permita navegar en una economa global que se caracteriza por la hiperconectividad, la abundancia de informacin y el rpido cambio tecnolgico.', '2020-12-21 09:39:24', '2020-12-21 09:39:24', NULL);
