INSERT INTO public.iniciativa_actor VALUES (264, 'Marca Patito', NULL, 'https://www.marcapatito.org/', 'Marca Patito es el primer canal de ventas basado en economa circular, al eliminar el plstico de su cadena de valor. Adicionalmente, reemplazan todos los materiales derivados del petrleo por alternativas sostenibles y renovables.', '2021-05-23 22:35:17', '2021-05-23 22:35:17', NULL);
