INSERT INTO public.iniciativa_actor VALUES (286, 'ReciVeci', 'ReciVeci', 'http://reciveci.ec/', 'Fomentar la separacin adecuada de residuos slidos en los hogares, entidades educativas y empresas en Ecuador, con el objetivo de generar prcticas inclusivas con las /os recicladoras/es base. Esto lo realizan mediante: capacitaciones, anlisis, asesoras y desarrollo de herramientas organizativas. Estas acciones y herramientas contribuyen a mitigar los riesgos ambientales y de salud ocupacional de las recicladoras y recicladores base cuando realizan su labor.', '2021-06-20 11:48:46', '2021-06-20 11:48:46', NULL);
