INSERT INTO public.iniciativa_actor VALUES (290, 'Laboratorios Bag Ecuador - Pacto Global Ecuador', 'Bag', 'https://ods3.org/', 'Es un laboratorio farmacutico que ha liderado el ODS 3 Salud y Bienestar por dos periodos consecutivos (2018 -2019) y (2019-2020), en la Red Pacto Global Ecuador. Uno de los productos entregables de la mesa del ODS3, es la Poltica de Salud y Bienestar ODS 3', '2021-06-20 20:20:18', '2021-06-20 20:20:18', NULL);
INSERT INTO public.iniciativa_actor VALUES (291, 'Empresa Pblica UPEC Creativa', 'UPEC-CREATIVA EP', 'https://www.upec-creativa.com/', 'UPEC-CREATIVA EP es una empresa pblica que provee bienes y servicios, que generan soluciones creativas, innovadoras y confiables, basadas en la investigacin y el conocimiento. Tiene por objeto la administracin y gestin logstica, operativa y comercial de bienes y servicios; consultora especializada y productos de proyectos de investigacin; adquisiciones; ejecucin de obras y prestacin de servicios; administracin de los bienes inmuebles y muebles y de las diferentes operaciones comerciales y de negocios que supongan una relacin y vinculacin de base comercial con entes pblicos o privados externos a la Universidad y con la propia Universidad.', '2021-06-22 16:14:04', '2021-06-22 16:14:04', NULL);
