INSERT INTO public.iniciativa_actor VALUES (293, 'Programa de las Naciones Unidas para el Desarrollo Ecuador', 'PNUD Ecuador', 'https://www.ec.undp.org/', 'El PNUD es parte de la red mundial de las Naciones Unidas que trabaja para lograr el desarrollo humano sostenible en el Ecuador, impulsando las capacidades y los esfuerzos nacionales para construir una sociedad equitativa a travs de la reduccin de la pobreza y la promocin de los derechos humanos y la gobernabilidad democrtica.', '2021-06-25 17:56:10', '2021-06-25 17:56:10', NULL);
