INSERT INTO public.iniciativa_actor VALUES (294, 'Fundacin Tierra para Todos', NULL, 'https://fundaciontierraparatodos.org/web/#', 'Tierra para Todos nace como una respuesta a diferentes necesidades identificadas en pueblos, nacionalidades y comunidades indgenas, afroecuatorianas y mestizas de la Imbabura y del cordn fronterizo con Colombia, con la misin de contribuir para el desarrollo sostenible a travs del fomento de medios de vida, la cohesin social y la bsqueda permanente de la paz a travs del dialogo y del fortalecimiento de las organizaciones de la sociedad civil y del Estado ecuatoriano.', '2021-06-25 20:33:58', '2021-06-25 20:33:58', NULL);
