INSERT INTO public.iniciativa_actor VALUES (298, 'Colectivo Central Dogma', NULL, 'https://guayungashub.org/', 'Organizacin de base comunitaria conformada por un grupo de artistas, activistas y pensadoras locales que crean puentes entre los artistas, la institucin y la sociedad civil. Su misin es compartir, tender puentes entre los artistas contemporneos, las instituciones y la sociedad civil, creando proyectos que generen conocimiento y que sean fuente de inspiracin y creatividad, fomentando el trabajo cooperativo, aportando valor econmico y social al movimiento cultural ecuatoriano y latinoamericano.', '2021-06-26 17:25:52', '2021-06-26 17:25:52', NULL);
