INSERT INTO public.iniciativa_actor VALUES (310, 'Fundacin Mashcana', NULL, 'https://mashcana.org/', 'La Fundacin Mashcana es un grupo de trabajo convencido de mantener una relacin de respeto entre los seres vivos, reivindicando la cultura y sabidura milenarias, buscando alternativas para conservar o mejorar la calidad de vida mediante el uso de energas renovables y tecnologas apropiadas.', '2021-07-02 20:17:15', '2021-07-02 20:17:15', NULL);
INSERT INTO public.iniciativa_actor VALUES (321, 'Gobierno Autnomo Descentralizado del Cantn Guaranda', NULL, 'http://www.guaranda.gob.ec/newsiteCMT/', 'El Gobierno Autnomo Descentralizado del Cantn Guaranda planifica, gestiona y administra el bienestar y desarrollo social, cultural, econmico de su comunidad entregando con honestidad y eficiencia prestaciones y servicios colectivos de calidad con el firme compromiso de mantener una ciudad digna para vivir, trabajar e invertir.', '2021-07-16 18:09:45', '2021-07-16 18:09:45', NULL);
