INSERT INTO public.iniciativa_actor VALUES (70, 'StartUp Grind Guayaquil', NULL, 'https://www.startupgrind.com/guayaquil/', 'StartUp Grind powered by Google for Entrepreneurs es una fantstica iniciativa diseada para Educar, Inspirar y Conectar a Emprendedores y al Ecosistema de Emprendimiento. nete! El Startup Grind es una comunidad global donde se aprende, se inspira y conecta con otros emprendedores por medio de conversatorios liderados por fundadores o inversionistas locales e internacionales. Tenemos un Spotlight Financiero en el que te damos tips tiles para tu emprendimiento. Adems, presentamos a emprendedores en las reas de fintech, productos y servicios.', '2020-12-21 09:43:36', '2020-12-21 09:43:36', NULL);
