INSERT INTO public.iniciativa_informacion VALUES (210, 'Campaa soberana alimentaria', 2020, 'on', '1616717373_iniciativas-070933.PNG', 'Utilizacin de plataformas digitales para la difusin de la campaa, posicionando ideas acerca de la importancia de la transformacin del sistema agroalimentario como alternativa frente al modelo agroindustrial extractivista.', 'Se propone visibilizar las acciones y agendas que plantean las organizaciones del campo y de mujeres, en torno a los modelos de vida que se plantean dentro del contexto electoral en Ecuador y que tienen que ver con los modelos de desarrollo rural en el pas.', 'https://www.facebook.com/Ocaruec/', 'https://twitter.com/OCARUEc', NULL, NULL, 'https://www.instagram.com/ocaru_ecuador/', '2021-03-25 19:09:33', '2021-07-10 10:25:39');
