INSERT INTO public.iniciativa_informacion VALUES (219, 'Prosperidad viral', 2020, 'on', '1618709208_iniciativas-082648.png', 'Con el uso de la ciencia de datos, las plataformas tecnolgicas y la capacitacin para el desarrollo de capacidades, este proyecto busca potenciar el espritu empresarial al brindar las herramientas y el conocimiento para hacer que los pequeos negocios sean sostenibles y minimizar los riesgos de fallas.', 'Prosperidad Viral es un proyecto que propone una solucin integral para empoderar a los pequeos y microempresarios que forman parte de los cuatro deciles ms bajos de la poblacin.', 'https://www.facebook.com/quidecuador', 'https://twitter.com/quidecuador', 'https://www.linkedin.com/company/quid-ecuador/', NULL, 'https://www.instagram.com/quidecuador/', '2021-04-17 20:26:48', '2021-07-10 10:47:50');
