INSERT INTO public.iniciativa_informacion VALUES (290, 'Escuela Granja', 2016, 'SI', '1621902131_iniciativas-072211.PNG', 'Proporcionar un sistema de acompaamiento pedaggico alternativo para los nios y nias con enfermedades catastrficas. Estructura social apoyada en la familia y el entorno inmediato del nio, que permita que los nios continen su proceso de desarrollo integral saludable.', 'El objetivo principal de la escuela granja es crear un espacio educativo integral tanto para los nios/as como para sus familias, proporcionando un desarrollo social competitivo y ofreciendo oportunidades, habilidades y destrezas que empoderen a estas poblaciones con el fin de que el espiral de la pobreza no se agudice.', 'https://www.facebook.com/FundacionCeciliaRivadeneira', 'https://twitter.com/juntossomosvida', NULL, 'https://www.youtube.com/user/FCRcomunicacion', 'https://www.instagram.com/fundacionceciliarivadeneira/', '2021-05-24 19:22:11', '2021-05-24 19:22:11');
