INSERT INTO public.iniciativa_informacion VALUES (349, 'Crecitex', 2015, 'SI', '1626835377_iniciativas-094257.png', 'Iniciativa de la ciudad de Ibarra, Ecuador, ganadora de Banco de ideas 2017 de la Secretara de Educacin Superior, Ciencia, Tecnologa e Innovacin consistente en recolectar desechos textiles y someterlos a un proceso de desfibrado y empezar nuevamente los procesos industriales', 'Hasta la actualidad se observa que la preocupacin del tratamiento de los desechos textiles se focaliza nicamente a los producidos en las empresas manufactureras. Sin embargo, es poco visibilizada la situacin provocada por el consumo de los productos textiles que se llevan a los hogares y que luego de su vida til no se sabe qu hacer con ellos.', 'https://www.facebook.com/Crecitex-517288748426253/', NULL, NULL, NULL, NULL, '2021-07-20 21:42:57', '2021-07-20 21:42:57');
