INSERT INTO public.materialesaprendizaje VALUES (158, 'El Comercio Exterior Del Ecuador: Analisis Del Intercambio De Bienes Desde La Colonia Hasta La Actualidad', '2012-07-02', '4', '4', false, 'https://ideas.repec.org/a/erv/observ/y2012i1737.html', 'Darwin Ordoez Iturralde', false, true, 90, NULL, '2021-07-27 00:18:14', '2021-07-27 00:18:14', 'Este breve recorrido por la historia del comercio exterior del Ecuador permite entonces mediante el anlisis del comportamiento pasado demostrar que un verdadero desarrollo no se logra exclusivamente mediante la exportacin de productos primarios y menos de productos que en su mayor parte son precio aceptante. El Ecuador perdi cinco siglos enmarcado en una poltica sin horizonte a futuro que le hubiera permitido alcanzar mayor grado de desarrollo.');
