INSERT INTO public.convocatorias VALUES (1, 1, 'ANÁLISIS DEL SECTOR PALMICULTOR DEL ECUADOR', '2020-12-05', '2021-02-28', 'Ayúdanos a analizar el sector palmicultor en Ecuador. Actualmente existen 6.568 palmicultores, de los que el 87,1% son dueños de pequeñas áreas de cultivo con plantaciones menores a 50 hectáreas, que están en las provincias de Esmeraldas, Sucumbíos, Orellana, Santo Domingo de los Tsáchilas, Pichincha, Imbabura, Cotopaxi, Bolívar, Los Ríos, Guayas y Manabí.', '1607182696_convocatoria_1_1.png', true, 11, '2020-12-05 10:38:15', '2021-07-13 14:17:42', '2021-07-13 14:17:42');
