INSERT INTO public.iniciativa_actor VALUES (97, 'INNOBIS', 'INNOBIS', 'https://www.innobis.ec', 'Innobis es una iniciativa que busca promover el emprendimiento y la innovación desde el Ecuador.  Innobis buscará apoyar el desarrollo de grandes proyectos emprendedores para convertirse en empresas exitosas con impacto global.', '2020-12-21 16:02:38', '2020-12-21 16:02:38', NULL);
