INSERT INTO public.iniciativa_actor VALUES (102, 'Zona Especial de Desarrollo Económico', 'ZEDE', 'http://www.espol.edu.ec', 'Una estrategia establecida por ley para estimular la innovación en la producción de bienes, servicios o procesos destinados principalmente a la exportación y a la sustitución de importaciones, mediante la instalación de nuevas empresas de base tecnológica. Las leyes establecen importantes ventajas para las empresas que se instalen en las ZEDE´s y favorecen una dinámica de colaboración entre el sector industrial y empresarial, y las universidades y los centros de investigación.', '2020-12-21 18:18:46', '2020-12-21 18:18:46', NULL);
