INSERT INTO public.iniciativa_actor VALUES (162, 'Idukay', 'Idukay', 'https://idukay.com/#inicio', 'Idukay es una plataforma para la gestión académica de colegios adaptada especialmente a las instituciones del Ecuador Esta herramienta fue diseñada de acuerdo a los parámetros de sencillez de uso, estabilidad y cumplimiento de los lineamientos y estándares del Ministerio de Educación.', '2020-12-22 07:23:55', '2020-12-22 07:23:55', NULL);
INSERT INTO public.iniciativa_actor VALUES (136, 'Transforme', NULL, 'https://www.transforme.cl/servicios.html', 'Fundada en 2001, ​Transforme es una firma de servicios profesionales especializados que implementa diversos tipos de Programas de Gestión de la Innovación en grandes empresas de Latinoamérica. Contamos con amplia experiencia en Chile, Argentina, Perú, Panamá, Colombia, Ecuador, Bolivia y México, consolidándonos como una firma seria, comprometida con sus clientes, y que sabe y confía en lo que hace para agregar valor a nuestros clientes.', '2020-12-21 23:21:03', '2020-12-21 23:21:03', NULL);
INSERT INTO public.iniciativa_actor VALUES (121, 'Secretaría de Movilidad', NULL, 'https://secretariademovilidad.quito.gob.ec/index.php', 'La Secretaría de Movilidad y sus dependencias adscritas -Empresa Pública Metropolitana de Movilidad y Obras Públicas, Empresa Pública Metropolitana de Transporte de Pasajeros de Quito, Empresa Pública Metropolitana Metro de Quito y Agencia Metropolitana de Tránsito-, emprenden un trabajo amplio e integral, con el despliegue de acciones que contribuyen al desarrollo y fortalecimiento del sistema vial de transporte; pero siempre pensando en la reivindicación del carácter ciudadano, y favorecer la convivencia armónica entre todos los usuarios en un espacio público de calidad.', '2020-12-21 22:42:02', '2020-12-21 22:42:02', NULL);
