INSERT INTO public.iniciativa_informacion VALUES (150, 'Crece Ecuador', 2018, 'on', '1608613436_iniciativas-120356.svg', '¨Plataforma web de financiamiento colaborativo con las menores tasas de interés posibles para emprendedores.', 'Crece ecuador es una plataforma virtual de anuncio de emprendimientos y financiamiento colaborativo donde se encuentran personas que tienen un negocio en funcionamiento y excelente trayectoria con inversionistas que están en búsqueda de emprendedores a quienes dar apoyo o financiar de forma colectiva.', NULL, NULL, NULL, NULL, NULL, '2020-12-22 00:03:56', '2020-12-27 11:51:04');
INSERT INTO public.iniciativa_informacion VALUES (149, 'Ecuaprotesis 3D', 2014, 'on', '1608613343_iniciativas-120223.svg', 'Elaboración de prótesis, órtesis y otros productos de ortopedia para personas con capacidades físicas reducidas; las cuales se obtienen en impresoras 3D.', 'Ecuaprotesis se caracteriza por ofrecer prótesis de acuerdo a la medida del paciente, las cuales son tomadas a través de un scanner 3D, además de ser prótesis livianas son de fácil uso, con diseños y colores escogidos por el paciente y de bajo costo en relación a prótesis de similares', NULL, NULL, NULL, NULL, NULL, '2020-12-22 00:02:23', '2020-12-27 11:51:30');
